module.exports = function (hoodie) {

  return {
    'server.api.plugin-request': function (request, reply) {
      console.log('server.api.plugin-request hook called');

      console.log(util.inspect(hoodie.task));

      hoodie.task.emit('syrup:update');

      hoodie.task.on('syrup:updated', function(text) {
        reply(JSON.stringify({
          'status': 'ok',
          'manifest': text
        }));
      });

      return true;
    }
  };
};
